sentences = ["""Ke esit mmɔ kpukpru, Abasi Ekpenyɔŋ ekedi ata ŋkpɔ uto.""","""Edi ikureke ke oro.""","""Ubom ikebeheke kaŋa ke isɔŋ ke ini emi Akpan nnyin ɔfiɔŋ okofioride ete, ‘ Oku Ndem Inyaŋ ! Abasi Ekpenyɔŋ do ke ubom oro ?""","""Mme awat ubom ema ewat ntre kaŋa suŋ suŋ, ndobo ndobo baba owo kiet inamke uyom tutu mmɔ ekpɔŋ esuk Abana ke ata anyan ebiet esuk ekewɔrɔ ke Akwa Akpa.""","""Ke ini emi Abasi Ekpenyɔŋ ama okoyorode ikwɔ ama, Okɔrɔyɔk eti akpatre ikɔ emi enye eketiŋde ke efe ɔnɔ Akpan nnyin Ofiɔŋ ete Ambo Eta ama ɔnyɔŋ edi yak etobo enye.""","""Akpatre owo ofiori ete, ‘ Ebine enye ! Emum ! Ekuyak ɔbɔhɔ!""","""Nso utɔ iyaresit inuk fi isin ke utɔ oburobut ido eke etiede ntem ?""","""Abasi Ekpenyɔŋ ke aŋwana ndimenede Asa ɔbɔŋ ke enyɔŋ man enye etim ɔfiɔk mme enye edede, mme akpakpa, owo kiet ke usuhɔde efak obio ofiori ke ɔkpɔsɔŋ uyo ete, ‘ Oro o ?""","""Ima ke otop ikaŋ, Owo ke ada ese Ibupke bup ntak Imenke ofut ikɔŋɔ.""","""Ko o ke mfuŋmfuŋ mbubreyo, ke ŋkpɔ nte ŋkanika ebede itiaba, ikɔt Abasi Ekpenyɔŋ ema eda ekedian ke esuk Abana.""","""Esɔ Nta ! inɔ ukot unam !""","""Abasi Ete, nso inam afo mudiaŋakede ke Ine ukpɔŋ ɔbɔŋ ?""","""Ntak edi oro ɔkɔnɔ akakade Esop -Nditɔ-Isɔŋ ke usen oro ; mme mbɔŋ obio ekenyuŋ eyakde enye abuana do ye mmɔ.""","""Nte ɔkɔnɔ akamamana ke Abana, baba owo kiet akananam inɔhɔ enye utɔ usɔŋ enyin oro. Akpa ekedi oro utɔ esuene eketiede ntre ekesimde enye.""","""Se ini Ufan tetie !""","""Ikɔ Abasi Ekpenyɔŋ ama ɔduɔ ɔtɔ ɔkɔnɔ nte obuma onyuŋ etimede enye idem eti eti.""",]
